import React, { useEffect, useState } from 'react'
import { useRouter } from 'next/router'

import Burger from './burger'
import styles from './styles/header.module.css'

import { Button } from 'antd'
import { UserOutlined } from '@ant-design/icons'

import firebase from '../firebaseConfig'

const Header = () => {
  const router = useRouter()
  const [toggleNavBar, setToggleNavBar] = useState(true)
  const [user, setUser] = useState(null)

  useEffect(() => {
      async function getUser() {
          let user = await firebase.auth().currentUser
          console.log(user)
          return user
      }
      setUser(getUser())
  }, [])

  const toggleNavBarFunction = () => {
    setToggleNavBar(!toggleNavBar)
  }

  const style = (ruta) => {
    if(router.pathname === ruta) {
      return styles.mobileNavBar_Items_Selected
    } else {
      return styles.mobileNavBar_Items
    }
  }

  const userSession = () => {
    if(!user) {
      return(
        <div>
          <UserOutlined />
        </div>
      )
    } else {
      return(
        <div>
          <UserOutlined />
        </div>
      )
    }
  }

  return (
  <div>
      <div style={{ display: "flex", alignItems: "center", height: "90px", justifyContent: "space-between" }}> 

        <div>
          <img src="/logo_tardan.png" alt="Logo de sombreros tardan"
          style={{ height: "50px" }}  />
        </div>

        <div>
          <div style={{ display: "flex", alignItems: "center" }}>

              <div style={{ display: "flex", alignItems: "center" }}>
                <div onClick={() => router.push('/')} className={style("/")} style={{ border: "none", fontSize: "1rem" }}>TIENDA</div>
                <div onClick={() => router.push('/nosotros')} className={style("/nosotros")} style={{ border: "none", fontSize: "1rem" }}>NOSOTROS</div>
                <div onClick={() => router.push('/contactanos')} className={style("/contactanos")} style={{ border: "none", fontSize: "1rem" }}>CONTACTANOS</div>
              </div>

            {userSession()}

            <div style={{ position: "relative", margin: "1rem" }}>
              <Button type="primary" shape="circle" style={{ zIndex: "100", position: "absolute", color: "white", top: "30px", left: "35px", borderRadius: "50%", textAlign: "center" }}><p>0</p></Button>
              <img src="/carrito.svg" alt="Bolsa de compras"  style={{ width: "30px", margin: "1rem", position: "relative", zIndex: "50" }} />
            </div>

              <div style={{ padding: "1rem" }}>
                <Burger toggle={toggleNavBarFunction} />
              </div>

          </div>
        </div>

      </div>

        <div className={styles.entradaNavBar}>
          <div>
            <div onClick={() => router.push('/')} className={style("/")}>TIENDA</div>
            <div onClick={() => router.push('/nosotros')} className={style("/nosotros")}>NOSOTROS</div>
            <div onClick={() => router.push('/contactanos')} className={style("/contactanos")}>CONTACTANOS</div>
          </div>
        </div>
  </div>
  );
}

export default Header;
