import React, { useEffect, useState } from 'react'
import { useRouter } from 'next/router'
import styles from './styles/header.module.css'

import { Button } from 'antd'
import { UserOutlined, ShoppingCartOutlined } from '@ant-design/icons'
import { Typography } from 'antd'

import firebase from '../firebaseConfig'

const Header = () => {
  const router = useRouter()
  const [toggleNavBar, setToggleNavBar] = useState(true)
  const [user, setUser] = useState(null)

  useEffect(() => {
      async function getUser() {
          let user = await firebase.auth().currentUser
          console.log(user)
          return user
      }
      setUser(getUser())
  }, [])

  const toggleNavBarFunction = () => {
    setToggleNavBar(!toggleNavBar)
  }

  const { Title } = Typography

  const navBarStyleItem = (route, name) => {
    if(router.pathname === route) {
      return <Typography level={4} onClick={() => router.push(route)}>{name}</Typography>
    } else {
      return <Typography level={4} onClick={() => router.push(route)}>{name}</Typography>
    }
  }

  return (
  <div>
      <div style={{ display: "flex", alignItems: "center", height: "90px", justifyContent: "space-between" }}> 

        <div>
          <img src="/logo_tardan.png" alt="Logo de sombreros tardan"
          style={{ height: "50px" }}  />
        </div>

        <div>
          <div style={{ display: "flex", alignItems: "center" }}>

              <div style={{ display: "flex", alignItems: "center" }}>
                <div styles={{ margin: "1rem" }}>
                  {navBarStyleItem("/", "TIENDA")}
                </div>
                {navBarStyleItem("/nosotros", "NOSOTROS")}
                {navBarStyleItem("/contacto", "CONTACTO")}
              </div>

              <UserOutlined />

            <div style={{ position: "relative", margin: "1rem" }}>
              <Button type="primary" shape="circle" style={{ zIndex: "100", position: "absolute", color: "white", top: "30px", left: "35px", borderRadius: "50%", textAlign: "center" }}><p>0</p></Button>
              <ShoppingCartOutlined style={{ width: "30px", margin: "1rem", position: "relative", zIndex: "50" }} />
            </div>

          </div>
        </div>

      </div>

        {/* <div className={styles.entradaNavBar}>
          <div>
            <div onClick={() => router.push('/')} className={style("/")}>TIENDA</div>
            <div onClick={() => router.push('/nosotros')} className={style("/nosotros")}>NOSOTROS</div>
            <div onClick={() => router.push('/contactanos')} className={style("/contactanos")}>CONTACTANOS</div>
          </div>
        </div> */}
  </div>
  );
}

export default Header;
