import React, { useState } from 'react'
import { useRouter } from 'next/router'

import { useFormik } from'formik'
import * as Yup from 'yup'

import { Form, Input, Button, Checkbox } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';

import firebase from '../../firebaseConfig'

export default function() {
    const router = useRouter()
    const [error, setError] = useState(null)

    const formik = useFormik({
        initialValues: {
            email: '',
            password: ''
        },
        validationSchema: Yup.object({
            email: Yup.string()
                .email('Email no valido')
                .required('La contraseña es obligatoria'),
            password: Yup.string()
                .required('La contraseña es obligatoria')
        }),
        onSubmit: async valores => {
            const { email, password } = valores;

            try {
                await firebase.auth().signInWithEmailAndPassword(email, password)
                router.push('/')
            } catch(e) {
                console.log(e)

                if(e.message === "The password is invalid or the user does not have a password.") {
                    setError("Contraseña incorrecta")
                }
            }
        }
    })

    const onFinish = async values => {
        const { email, password } = values;

            try {
                await firebase.auth().signInWithEmailAndPassword(email, password)
                router.push('/')
            } catch(e) {
                console.log(e)

                if(e.message === "The password is invalid or the user does not have a password.") {
                    setError("Contraseña incorrecta")
                }
            }
      };

    return(
        <div style={{ width: "100%", textAlign: "center" }}>
            <div style={{ backgroundColor: "rgb(247, 247, 247)", width: "400px", margin: "3rem auto" }}>
                <div>
                    <img style={{ marginTop: "3rem" }} src="/logo.png" alt="Logo" />
                    <div style={{ marginTop: "1rem", fontSize: "1.4rem", color: "rgba(0, 0, 0, 0.65)" }}>Inicia Sesión en Tardan</div>
                </div>
                <div style={{ width: "90%", textAlign: "center", margin: "0 auto", padding: "2rem 0" }}>
                <Form
                name="normal_login"
                className="login-form"
                initialValues={{
                    remember: true,
                }}
                onFinish={onFinish}
                >
                <Form.Item
                    name="email"
                    rules={[
                    {
                        required: true,
                        message: 'Ingresa tu Correo Electronico!',
                    },
                    {
                        type: 'email',
                        message: 'Ingresa un correo valido'
                    }
                    ]}
                >
                    <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Correo Electronico" />
                </Form.Item>
                <Form.Item
                    name="password"
                    rules={[
                    {
                        required: true,
                        message: 'Ingresa tu contraseña',
                    },
                    ]}
                >
                    <Input
                    prefix={<LockOutlined className="site-form-item-icon" />}
                    type="password"
                    placeholder="Contraseña"
                    />
                </Form.Item>
                <Form.Item>
                    <Button type="primary" htmlType="submit" className="login-form-button" style={{ width: "80%" }}>
                        Log in
                    </Button>
                </Form.Item>
                <Form.Item>
                    <a className="login-form-forgot" href="">
                    Restablecer contraseña
                    </a>
                    <br /> O <br />
                    <a href="">Registrarse ahora!</a>
                </Form.Item>
                </Form>
                </div>
            </div>
        </div>
    )
}

