import React, { useState, useEffect } from 'react'
import { UserOutlined } from '@ant-design/icons'
import { Popover } from 'antd'

import firebase from '../firebaseConfig'

export default function() {
    const [user, setUser] = useState(null)

    useEffect(() => {
        async function getUser() {
            try {
                await firebase.auth().onAuthStateChanged(function(user) {
                    if(user) {
                        setUser(user.email)
                    } else {
                        setUser(null)
                    }
                })
            } catch(e) {
                console.log(e)
            }
        }
        getUser()
    }, [])

    const content = (
        <div>
            {user && (
                <div>
                    <div style={{ color: "#93a9b5", fontSize: "1rem", borderBottom: "1px #e5edef solid", paddi: "0.5rem" }} className="click">Perfil</div>
                    <div style={{ color: "#ff4d4f", fontSize: "1rem" }} className="click">Cerrar sesión</div>
                </div>
            )}
            {!user && (
                <div>
                    <div style={{ color: "#93a9b5" }}>Iniciar sessión</div>
                    <div style={{ color: "#93a9b5" }}>Cerrar sessión</div>
                </div>
            )}
        </div>
    )

    const title = () => {
        console.log("title")
        if(user) return <div style={{ color: "#93a9b5" }}>{user}</div>
    }
    return(
        <Popover content={content} placement="bottom" title={title()}>
            <UserOutlined style={{ fontSize: "1.5rem", color: "#93a9b5" }} className="click" />
        </Popover>
    )
}
