    const [user, setUser] = useState(null)

    useEffect(() => {
        async function getUser() {
            let user = await firebase.auth().currentUser
            console.log(user)
            return user
        }
        setUser(getUser())
    }, [])

    if(!user) return <div>Cargando...</div>

    if(user) return <div>user</div>
}
