    const [user, setUser] = useState(null)

    const query = `
    query usuario($input: IdInput!) {
        getUser(input: $input) {
            name
            email
        }
    }
    `

    useEffect(() => {
        firebase.auth().onAuthStateChanged(function(userToken) {
            const variables = {
                input: {
                    id: userToken.uid
                }
            }

